import React from 'react'
import { useDispatch, useSelector } from 'react-redux'

const Incomplete = (props) => {
    const dispatch=useDispatch()
    const state=useSelector(state=>state)
  return (
    <>
         <ul id="Incomplete">
         {
                state.todos.filter(x=>x.status==="InComplete").map((data, i) => <li> <input type="checkbox" index={i} name="completed" onClick={this.props.actioner} checked /><label>{data}</label><input type="text" id="name" className="name" value={data} /><button className="edit" index={i} name="completed" onClick={this.props.edit}>Edit</button><button onClick={this.props.delete} index={i} name="completed" className="delete">Delete</button> </li>)
                }
         </ul> 
         <h3>Complete</h3>
         <ul id="Complete">
         {
                state.todos.filter(x=>x.status==="InComplete").map((data, i) => <li> <input type="checkbox" index={i} name="completed" onClick={this.props.actioner} checked /><label>{data}</label><input type="text" id="name" className="name" value={data} /><button className="edit" index={i} name="completed" onClick={this.props.edit}>Edit</button><button onClick={this.props.delete} index={i} name="completed" className="delete">Delete</button> </li>)
                }
         </ul>      
    </>
  )
}

export default Incomplete
