import "./App.css";
import MainFunc from "./component/MainFunc";
import store from "./features/store";
import { Provider } from "react-redux";
import { MyContext } from "./tools/MyContext";
import { useState } from "react";
const initialState = {
  todos: [],
  index: -1,
  btnVal: "ADD",
};
function App() {
  const [state, setState] = useState(initialState);
  const addAction = (action) => {
    state.todos.push(action);
    state.Sno = state.Sno + 1;
    setState({ ...state });
  };
  const updateAction = (action) => {
    state.todos[action.index].text = action.text;
    state.index = -1;
    state.btnVal = "Add";
    setState({ ...state });
  };
  const deleteAction = (action) => {
    state.todos.splice(action, 1);
    setState({ ...state });
  };
  const editMode = (action) => {
    state.btnVal = "Update";
    state.index = action;
    setState({ ...state });
  };
  const cheker = (action) => {
    state.todos[action.index].status = action.status;
    setState({ ...state });
  };
  return (
    <>
      <MyContext.Provider value={{ state, setState }}>
        <MainFunc />
      </MyContext.Provider>
    </>
  );
  // return (
  //   <>
  //   <Provider store={ store }>
  //     <MainFunc />
  //     </Provider>
  //   </>
  // );
}

export default App;
