import React from 'react'
import { useDispatch, useSelector } from 'react-redux'

const Incomplete = (props) => {
    const dispatch=useDispatch()
    const state=useSelector(state=>state)
  return (
    <>
         {
                   state.todo.map((data, i) => <li> <input type="checkbox" name="incompleted" onClick={props.Checeker} unchecked /><label>{data}</label><input type="text" id="name" className="name" value={data} /><button className="edit"  name="incompleted" onClick={()=>props.editClick(i)}>Edit</button><button onClick={this.props.delete} index={i} name="incompleted" className="delete">Delete</button> </li>)
                }
    </>
  )
}

export default Incomplete
